import { Component, EventEmitter, Output } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';

@Component({
  selector: 'app-prompt',
  template: `
    <form [formGroup]="form" (ngSubmit)="onSubmit()" class="flex items-center">
      <div class="relative flex-1">
        <textarea
          [formControl]="form.controls.prompt"
          (keyup.enter)="onSubmit()"
          tabindex="0"
          data-id="root"
          placeholder="Send a message..."
          class="w-full p-2 rounded-md border-gray-200 focus:outline-none focus:ring-2 focus:ring-blue-500 dark:focus:ring-white"
        ></textarea>
      </div>
      <button
        class="ml-3 px-3 py-2 rounded-md bg-blue-500 text-white hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed"
        type="submit"
        [disabled]="form.invalid"
      >
        <svg class="w-5 h-5" viewBox="0 0 20 20" fill="currentColor">
          <path
            fill-rule="evenodd"
            d="M17.707 2.293a1 1 0 010 1.414L6.414 16.414a1 1 0 01-1.414 0L2.293 13.707a1 1 0 011.414-1.414L13.586 14l3.707-3.707a1 1 0 011.414 0zM5 9a1 1 0 100 2h8a1 1 0 100-2H5z"
            clip-rule="evenodd"
          ></path>
        </svg>
      </button>
    </form>
  `,
  styles: [
    `
      form {
        background-color: #fff;
        border: 1px solid #e2e8f0;
        border-radius: 0.375rem;
        overflow: hidden;
      }

      textarea {
        height: 3rem;
        min-height: 3rem;
        resize: none;
      }
    `,
  ],
})
export class PromptComponent {
  @Output() public readonly prompted = new EventEmitter<string>();

  public readonly form = new FormGroup({
    prompt: new FormControl<string | null>(null, [Validators.required]),
  });

  public onSubmit(): void {
    if (this.form.valid && this.form.value.prompt) {
      this.prompted.emit(this.form.value.prompt);
      this.form.reset();
    }
  }
}
